import React from 'react';
import { useAppStore } from '../stores';

const ConnectionStatus: React.FC = () => {
  const { isConnected } = useAppStore();

  // In development/DevNet mode, don't show connection warnings
  // The backend health check handles connectivity
  if (process.env.NODE_ENV === 'development') return null;

  // Only show warning in production if truly disconnected
  if (isConnected) return null;

  return (
    <div className="bg-warning-100 border-l-4 border-warning-500 p-4 mb-4">
      <div className="flex">
        <div className="ml-3">
          <p className="text-sm text-warning-700">
            Connection issue detected. Attempting to reconnect...
          </p>
        </div>
      </div>
    </div>
  );
};

export default ConnectionStatus;
